import React, {useState, useEffect} from 'react'
import { Todo } from "./Todo";
import { TodoForm } from './TodoForm'
import { v4 as uuidv4 } from 'uuid';
import { EditTodoForm } from './EditTodoForm';
import api from '../api/axiosConfig';
uuidv4();

export const TodoWrapper = () => {

    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    const getTodos= async () => {

        try {
            const response = await api.get("/api/v1/todos");
            console.log(response.data);
            setTodos(response.data);
        } catch (err) {
            console.log(err);
        }
    }

    const addTodo = todo => {
        setTodos([...todos, {id: uuidv4(), task: todo, completed: false, isEditing: false}])
        console.log(todos)
    }

    const toggleComplete = id  => {
        setTodos(todos.map(todo => todo.id === id ? {...todo, completed: !todo.completed} : todo))
    }

    const deleteTodo = id => {
        setTodos(todos.filter(todo => todo.id !== id))
    }

    const editTodo = id => {
        setTodos(todos.map(todo => todo.id === id ? {...todo, isEditing: !todo.isEditing} : todo))
    }

    const editTask = async (updatedTask, id) => {
        try {
            const { title, description } = updatedTask;
            await api.put(`/api/v1/todos/${id}`, { title, description });
            setTodos(todos.map(todo => todo.id === id ? { ...todo, title, description, isEditing: !todo.isEditing } : todo));
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        getTodos();
    },[])

    return (
        <div className='TodoWrapper'>
            <h1>Get things done!</h1>
            <TodoForm addTodo={addTodo}/>

            <h2>My Todos</h2>
            {todos.map(todo => (
                todo.isEditing ? (
                    <EditTodoForm editTodo={editTask} task={todo}/>
                ) : (
                    <Todo task={todo} key={todo.id} 
                    toggleComplete={toggleComplete} deleteTodo={deleteTodo} editTodo={editTodo}/>
                )
            ))}
        </div>
    )
}
