import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faTrash, faSquareCheck } from '@fortawesome/free-solid-svg-icons';

export const Todo = ({ task, toggleComplete, deleteTodo, editTodo }) => {
  const handleToggleComplete = () => {
    toggleComplete(task.id);
  };

  return (
    <div className='Todo'>
        <div>
            <p className='todo-title'>{task.title}</p>
            <div className='todo-description'>{task.description}</div>
        </div>
        <div>
        <FontAwesomeIcon className='complete-icon' icon={faSquareCheck} onClick={() => 
            toggleComplete(task.id)} className={`${task.completed ? "completed" : ""}`}/>
        <FontAwesomeIcon className='edit-icon' icon={faPenToSquare} onClick={() => editTodo(task.id)}/>
        <FontAwesomeIcon className='delete-icon' icon={faTrash} onClick={() => deleteTodo(task.id)}/>
      </div>
    </div>
  );
};
